// src/components/Navbar.jsx
import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

export default function Navbar() {
  const navigate = useNavigate();

  return (
    <motion.header
      initial={{ y: -18, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.45, ease: "easeOut" }}
      className="w-full sticky top-0 z-50"
      style={{ backdropFilter: "none" }} // explicit: no heavy blur
    >
      <div className="max-w-7xl mx-auto px-6 py-3 flex items-center justify-between">
        {/* Brand */}
        <div
          onClick={() => navigate("/")}
          className="flex items-center gap-3 cursor-pointer select-none"
        >
          <div
            className="w-10 h-10 rounded-full flex items-center justify-center font-bold shadow"
            style={{
              background: "linear-gradient(90deg,#ff6b6b,#c81f1f)",
              color: "white",
            }}
          >
            HC
          </div>
          <div className="text-lg font-semibold text-hc-700">HealthChain</div>
        </div>

        {/* Nav links (transparent header so links show well) */}
        <nav className="hidden md:flex items-center gap-6">
          <motion.div whileHover={{ y: -2 }} className="text-sm text-gray-700 hover:text-hc-600 transition">
            <Link to="/">Home</Link>
          </motion.div>
          <motion.div whileHover={{ y: -2 }} className="text-sm text-gray-700 hover:text-hc-600 transition">
            <Link to="/about">About</Link>
          </motion.div>
          <motion.div whileHover={{ y: -2 }} className="text-sm text-gray-700 hover:text-hc-600 transition">
            <Link to="/select-role">Sign In</Link>
          </motion.div>
        </nav>

        {/* CTA */}
        <div>
          <motion.button
            whileHover={{ scale: 1.03 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => navigate("/select-role")}
            className="px-4 py-2 rounded-full text-white font-semibold"
            style={{
              background: "linear-gradient(90deg,#ff6b6b,#c81f1f)",
              boxShadow: "0 8px 18px rgba(200,30,30,0.18)",
            }}
          >
            Get Started
          </motion.button>
        </div>
      </div>
    </motion.header>
  );
}
