// src/pages/SelectRole.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

const ROLES = [
  {
    id: "patient",
    title: "Patient",
    desc: "View your medical history, prescriptions, and contact your doctor.",
    note: "Access your personal health records & appointments.",
  },
  {
    id: "doctor",
    title: "Doctor",
    desc: "Manage patients and update records (placeholder).",
    note: "Doctor features will be implemented later.",
  },
  {
    id: "pharmacy",
    title: "Pharmacy",
    desc: "Process prescriptions & manage orders (placeholder).",
    note: "Pharmacy features will be implemented later.",
  },
];

export default function SelectRole() {
  const [selected, setSelected] = useState(null);
  const navigate = useNavigate();

  const handleSelect = (roleId) => {
    setSelected(roleId);
    // persist role for SignIn page
    localStorage.setItem("hc_selectedRole", roleId);

    // small animated delay for UX then go to signin
    setTimeout(() => navigate("/signin"), 240);
  };

  return (
    <section className="min-h-[calc(100vh-72px)] bg-gradient-to-br from-hc-600 via-hc-500 to-hc-700 text-white flex flex-col items-center pt-20 pb-14 px-4">
      <motion.h2
        initial={{ y: -10, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.45 }}
        className="text-4xl font-extrabold mb-2"
      >
        Who are you?
      </motion.h2>

      <motion.p initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.12 }} className="max-w-2xl text-center text-red-100/90 mb-10">
        Choose a role to continue. (This demo fully supports the Patient flow.)
      </motion.p>

      <div className="w-full max-w-5xl grid grid-cols-1 sm:grid-cols-3 gap-6">
        {ROLES.map((r, idx) => (
          <motion.button
            key={r.id}
            onClick={() => handleSelect(r.id)}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: idx * 0.07, duration: 0.45 }}
            whileHover={{ scale: 1.03 }}
            whileTap={{ scale: 0.98 }}
            className={`relative p-6 rounded-2xl text-left cursor-pointer
              ${selected === r.id ? "bg-white text-red-700 shadow-2xl" : "bg-white/10 hover:bg-white/20"}
            `}
          >
            <div className="flex justify-between items-start">
              <div>
                <h3 className={`text-2xl font-bold ${selected === r.id ? "text-red-700" : "text-white"}`}>{r.title}</h3>
                <p className={`mt-2 text-sm ${selected === r.id ? "text-red-600" : "text-red-100/90"}`}>{r.desc}</p>
              </div>
              <div className={`w-12 h-12 rounded-full flex items-center justify-center ${selected === r.id ? "bg-red-100 text-red-700" : "bg-white/20 text-white"}`}>
                <span className="font-bold">{r.title.charAt(0)}</span>
              </div>
            </div>

            <div className={`mt-4 text-sm ${selected === r.id ? "text-red-500" : "text-red-100/80"}`}>{r.note}</div>

            {selected === r.id && (
              <motion.span initial={{ scale: 0 }} animate={{ scale: 1 }} className="absolute top-4 right-4 bg-red-600 text-white px-2 py-1 rounded text-xs font-semibold">
                Selected
              </motion.span>
            )}
          </motion.button>
        ))}
      </div>
    </section>
  );
}
