import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

const roles = [
  {
    id: "patient",
    title: "Patient",
    description: "View your medical history, appointments, and reports securely."
  },
  {
    id: "doctor",
    title: "Doctor",
    description: "Access patient profiles and update treatment records."
  },
  {
    id: "pharmacy",
    title: "Pharmacy",
    description: "Manage prescriptions and track medicine availability."
  }
];

export default function Login({ setIsLoggedIn, setRole }) {
  const [selectedRole, setSelectedRole] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    if (!selectedRole) return alert("Please select a role!");
    setRole(selectedRole);
    setIsLoggedIn(true);
    navigate("/dashboard");
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-red-600 via-red-500 to-red-800 text-white pt-20">
      <motion.h1
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
        className="text-4xl font-bold mb-10"
      >
        Select Your Role
      </motion.h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 w-4/5">
        {roles.map((r) => (
          <motion.div
            key={r.id}
            onClick={() => setSelectedRole(r.id)}
            whileHover={{ scale: 1.05 }}
            className={`cursor-pointer p-6 rounded-xl shadow-lg transition ${
              selectedRole === r.id ? "bg-white text-red-600" : "bg-red-700 text-white"
            }`}
          >
            <h2 className="text-2xl font-bold mb-2">{r.title}</h2>
            <p className="text-sm">{r.description}</p>
          </motion.div>
        ))}
      </div>

      <button
        onClick={handleLogin}
        className="mt-10 px-8 py-2 bg-white text-red-600 rounded-lg btn-hover"
      >
        Continue
      </button>
    </div>
  );
}
