// src/pages/Dashboard.jsx
import React, { useEffect, useState } from "react";
import API from "../services/api";
import { motion } from "framer-motion";

export default function Dashboard() {
  const [patient, setPatient] = useState(null);

  useEffect(() => {
    async function fetchPatient() {
      try {
        const res = await API.get("/patients/me");
        setPatient(res.data.patient);
      } catch (err) {
        console.error("Error fetching patient data", err);
      }
    }
    fetchPatient();
  }, []);

  if (!patient) return <div className="text-center mt-40 text-lg">Loading...</div>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-red-500 via-red-600 to-red-800 text-white p-10">
      <motion.div
        initial={{ y: -40, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.6 }}
        className="max-w-4xl mx-auto bg-white/10 p-8 rounded-2xl shadow-lg"
      >
        <h1 className="text-3xl font-bold mb-4">{patient.name}'s Dashboard</h1>
        <p>Age: {patient.age}</p>
        <p>Blood Group: {patient.bloodGroup}</p>
        <p>Allergies: {patient.allergies}</p>
        <p>Current Status: {patient.currentStatus}</p>

        <h2 className="mt-8 text-2xl font-semibold">Medicines</h2>
        <ul className="list-disc ml-8 mt-2">
          {patient.medicines.map((m, i) => (
            <li key={i}>{m}</li>
          ))}
        </ul>
      </motion.div>
    </div>
  );
}
