import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import api from "../services/api"; // <-- expects src/services/api.js
import { format } from "date-fns";

const FALLBACK = {
  patient: {
    name: "Madhan Kumar",
    age: 29,
    sex: "Male",
    bloodGroup: "B+",
    allergies: ["Penicillin"],
    emergencyContact: { name: "S. Kumar", relation: "Father", phone: "‪+91-9999999999‬" },
    avatar: "https://i.pravatar.cc/150?img=12",
    medicines: ["Paracetamol 500mg", "Azithromycin 500mg"],
    currentStatus: { status: "Fever", notes: "High temperature, advised rest", since: new Date().toISOString() },
    assignedDoctor: { name: "Dr. S. Raj", specialty: "General Physician", phone: "+91-1111111111", avatar: "https://i.pravatar.cc/150?img=3" }
  },
  medicalHistory: [
    { _id: "1", date: new Date().toISOString(), condition: "Seasonal flu", notes: "Rest & antivirals" },
  ],
  prescriptions: [
    { _id: "p1", createdAt: new Date().toISOString(), meds: [{ name: "Paracetamol", dosage: "500mg", frequency: "3x/day" }], notes: "Take after food", pharmacyStatus: "sent" }
  ],
  appointments: []
};

export default function PatientDetails() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    let mounted = true;
    async function fetchProfile() {
      setLoading(true);
      setError("");
      try {
        const res = await api.get("/patients/me");
        // expected response: { patient, medicalHistory, appointments, prescriptions }
        if (!mounted) return;
        setData(res.data);
      } catch (err) {
        console.error("Failed to fetch patient profile:", err);
        setError("Unable to load patient data. Showing demo data.");
        // fallback to demo
        setData(FALLBACK);
      } finally {
        if (mounted) setLoading(false);
      }
    }
    fetchProfile();
    return () => { mounted = false; };
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-red-700 via-red-600 to-red-800 text-white">
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ repeat: Infinity, duration: 1.4, ease: "linear" }}
          className="w-14 h-14 rounded-full border-4 border-white/30 flex items-center justify-center"
        >
          <div className="w-6 h-6 rounded-full bg-white/80" />
        </motion.div>
      </div>
    );
  }

  // Use fetched data or fallback
  const { patient, medicalHistory = [], prescriptions = [], appointments = [] } = data || FALLBACK;

  const sinceFormatted = patient?.currentStatus?.since
    ? format(new Date(patient.currentStatus.since), "dd MMM yyyy")
    : "N/A";

  return (
    <div className="min-h-screen p-8 bg-gradient-to-br from-red-700 via-red-600 to-red-800 text-white">
      <div className="max-w-4xl mx-auto">
        <motion.div
          initial={{ opacity: 0, y: -16 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="bg-white/8 backdrop-blur p-6 rounded-2xl shadow-xl border border-white/10"
        >
          <div className="flex items-center gap-6">
            <img
              src={patient.avatar || "/images/placeholder-avatar.png"}
              alt={patient.name}
              className="w-28 h-28 rounded-full object-cover border-4 border-white/20 shadow-md"
            />
            <div>
              <h1 className="text-3xl font-bold text-white">{patient.name}</h1>
              <p className="text-sm text-white/80">{patient.email || ""} • {patient.phone || ""}</p>
              <p className="mt-2 text-sm text-white/80">
                <strong>Assigned Doctor:</strong> {patient.assignedDoctor?.name || "Not assigned"}
              </p>
            </div>
            <div className="ml-auto text-right">
              <motion.button whileHover={{ y: -4 }} className="btn-gloss px-4 py-2 rounded-full text-sm">
                Message Doctor
              </motion.button>
            </div>
          </div>

          {/* bio grid */}
          <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="p-4 bg-white/6 rounded-lg">
              <h3 className="text-sm text-white/90 font-semibold">Personal</h3>
              <p className="text-white/80 mt-2">Age: {patient.age ?? "—"}</p>
              <p className="text-white/80">Sex: {patient.sex ?? "—"}</p>
              <p className="text-white/80">Blood Group: {patient.bloodGroup ?? "—"}</p>
            </div>

            <div className="p-4 bg-white/6 rounded-lg">
              <h3 className="text-sm text-white/90 font-semibold">Contact</h3>
              <p className="text-white/80 mt-2">Phone: {patient.phone ?? "—"}</p>
              <p className="text-white/80">Emergency: {patient.emergencyContact?.name ?? "—"} ({patient.emergencyContact?.phone ?? "—"})</p>
            </div>

            <div className="p-4 bg-white/6 rounded-lg">
              <h3 className="text-sm text-white/90 font-semibold">Status</h3>
              <p className="text-white/80 mt-2">{patient.currentStatus?.status || "No active condition"}</p>
              <p className="text-white/80 text-sm mt-1">{patient.currentStatus?.notes || ""}</p>
              <p className="text-white/70 text-xs mt-2">Since: {sinceFormatted}</p>
            </div>
          </div>

          {/* medicines & quick stats */}
          <div className="mt-6 grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="p-4 bg-white/6 rounded-lg">
              <h3 className="text-sm text-white/90 font-semibold">Current Medicines</h3>
              <ul className="mt-3 space-y-2">
                {patient.medicines && patient.medicines.length > 0 ? (
                  patient.medicines.map((m, i) => (
                    <li key={i} className="text-white/80">
                      • {m}
                    </li>
                  ))
                ) : (
                  <li className="text-white/70">No current medicines.</li>
                )}
              </ul>
            </div>

            <div className="p-4 bg-white/6 rounded-lg">
              <h3 className="text-sm text-white/90 font-semibold">Quick Stats</h3>
              <div className="flex items-center justify-between mt-4">
                <div>
                  <div className="text-2xl font-bold">1,000+</div>
                  <div className="text-xs text-white/70">Doctors on platform</div>
                </div>
                <div>
                  <div className="text-2xl font-bold">70,000+</div>
                  <div className="text-xs text-white/70">Patients registered</div>
                </div>
              </div>
            </div>
          </div>

          {/* appointments, prescriptions, history */}
          <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="p-4 bg-white/6 rounded-lg">
              <h4 className="text-sm font-semibold">Latest Appointment</h4>
              {appointments && appointments.length > 0 ? (
                <>
                  <p className="text-white/80 mt-2">{appointments[0].reason}</p>
                  <p className="text-xs text-white/70 mt-1">With: {appointments[0].doctor?.name}</p>
                </>
              ) : (
                <p className="text-white/70 mt-2">No appointments found.</p>
              )}
            </div>

            <div className="p-4 bg-white/6 rounded-lg">
              <h4 className="text-sm font-semibold">Recent Prescription</h4>
              {prescriptions && prescriptions.length > 0 ? (
                <>
                  <p className="text-white/80 mt-2">Prescribed by: {prescriptions[0].doctor?.name}</p>
                  <p className="text-white/80 text-sm mt-1">{prescriptions[0].meds?.map(m => m.name).join(", ")}</p>
                  <p className="text-xs text-white/70 mt-1">Status: {prescriptions[0].pharmacyStatus}</p>
                </>
              ) : (
                <p className="text-white/70 mt-2">No prescriptions yet.</p>
              )}
            </div>

            <div className="p-4 bg-white/6 rounded-lg">
              <h4 className="text-sm font-semibold">Medical History (recent)</h4>
              {medicalHistory && medicalHistory.length > 0 ? (
                <ul className="mt-2 space-y-2 text-white/80">
                  {medicalHistory.slice(0, 4).map((h) => (
                    <li key={h._id}>
                      <div className="font-medium">{h.condition}</div>
                      <div className="text-xs text-white/70">{h.notes}</div>
                    </li>
                  ))}
                </ul>
              ) : (
                <p className="text-white/70 mt-2">No medical records available.</p>
              )}
            </div>
          </div>

          {error && (
            <div className="mt-6 text-yellow-200 text-sm">
              {error} (Showing demo data)
            </div>
          )}
        </motion.div>
      </div>
    </div>
  );
}
