import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import Dashboard from "./pages/Dashboard";
import About from "./pages/About";
import SelectRole from "./pages/SelectRole";
import SignIn from "./pages/SignIn";
import PatientDashboard from "./pages/PatientDashboard";

function App() {
  const [role, setRole] = useState(null);
  const [user, setUser] = useState(null);

  return (
    <Router>
      <div className="min-h-screen">
        <Navbar />
        <Routes>
          {/* Homepage (glossy animated red) */}
          <Route path="/" element={<Dashboard />} />
          {/* About page */}
          <Route path="/about" element={<About />} />

          {/* Role select */}
          <Route path="/select-role" element={<SelectRole setRole={setRole} />} />
          {/* Sign in */}
          <Route path="/signin" element={<SignIn role={role} setUser={setUser} />} />

          {/* After login (patients only for now) */}
          <Route
            path="/patient/dashboard"
            element={user ? <PatientDashboard user={user} /> : <Navigate to="/signin" />}
          />

          {/* Fallback */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
