import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import api from "../api/axios";

export default function SignIn({ setUser }) {
  const navigate = useNavigate();
  const location = useLocation();
  const role = location.state?.role; // get role from location.state

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  if (!role) {
    navigate("/select-role"); // redirect if no role
    return null;
  }

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const res = await api.post("/auth/login", { username, password });
      localStorage.setItem("token", res.data.token);
      setUser(res.data.user);

      setTimeout(() => {
        if (res.data.user.role.toLowerCase() === "patient") navigate("/patient/dashboard");
        else navigate("/"); 
      }, 0);
    } catch (err) {
      setError(err.response?.data?.error || "Login failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-red-600 via-red-500 to-red-800 text-white">
      <div className="bg-white/10 backdrop-blur-xl p-10 rounded-2xl shadow-2xl w-96">
        <h2 className="text-3xl font-bold text-center mb-6">Sign In ({role})</h2>
        <form onSubmit={handleLogin} className="space-y-4">
          <input
            type="text"
            placeholder="Username"
            className="w-full px-4 py-2 rounded-md text-black"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            className="w-full px-4 py-2 rounded-md text-black"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button
            type="submit"
            disabled={loading}
            className="w-full py-2 bg-red-600 rounded-lg hover:bg-red-700 transition"
          >
            {loading ? "Loading..." : "Login"}
          </button>
          {error && <p className="text-red-300 text-center">{error}</p>}
        </form>
      </div>
    </div>
  );
}
