// src/pages/About.jsx
import React from "react";
import { motion } from "framer-motion";

/**
 * About page — glossy red theme, two animated cards.
 * Place developer images in: public/images/dev1.jpg and public/images/dev2.jpg
 */

const devs = [
  { name: "Leo Das", role: "Lead Developer", img: "/images/leo.png", bio: "Leo is passionate about building scalable healthcare solutions and leads the HealthChain project with a focus on clean code and user experience." },
  { name: "Parthiban", role: "Backend Engineer", img: "/images/parthiban.jpg", bio: "Parthiban specializes in secure backend systems and ensures HealthChain's data integrity and performance." }
];

export default function About() {
  return (
    <main className="min-h-[calc(100vh-72px)] p-12 bg-gradient-to-br from-hc-100 via-hc-300 to-hc-600 relative overflow-hidden text-white">
      {/* Soft glossy highlights (these are decorative and subtle) */}
      <div className="absolute inset-0 pointer-events-none z-0">
        <div style={{ mixBlendMode: "multiply" }} className="absolute left-[-10%] top-0 w-[40vw] h-[40vh] rounded-full bg-red-400/6 blur-3xl" />
        <div style={{ mixBlendMode: "screen" }} className="absolute right-[-8%] bottom-0 w-[36vw] h-[36vh] rounded-full bg-red-300/6 blur-3xl" />
      </div>

      <section className="relative z-10 max-w-6xl mx-auto">
        <motion.header
          initial={{ y: -20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.5 }}
          className="text-center mb-10"
        >
          <h1 className="text-4xl md:text-5xl font-extrabold text-hc-700 mb-3">About HealthChain</h1>
          <p className="max-w-2xl mx-auto text-muted">
            HealthChain is a demo healthcare platform — a clean, secure place to manage patient records,
            prescriptions and appointments with a glossy, modern UI and smooth micro-interactions.
          </p>
        </motion.header>

        <motion.div
          initial="hidden"
          animate="show"
          variants={{
            hidden: {},
            show: { transition: { staggerChildren: 0.14 } }
          }}
          className="grid grid-cols-1 md:grid-cols-2 gap-8 items-start"
        >
          {devs.map((d, i) => (
            <motion.article
              key={d.name}
              className="parallax bg-white/10 backdrop-blur-xl border border-white/10 p-6 rounded-2xl relative cursor-pointer z-10 hover:shadow-xl transition-all"
              style={{
                transform: "translateZ(0)",
                // subtle use of CSS vars (--mx/--my) gives a parallax tilt
                transformOrigin: "center",
              }}
              whileHover={{ scale: 1.03, y: -6 }}
              initial={{ opacity: 0, y: 18 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: i * 0.08 }}
            >
              <div className="flex items-center gap-4">
                <div className="w-20 h-20 rounded-full overflow-hidden flex-shrink-0 border-4 border-red-200">
                  <img src={d.img} alt={d.name} className="w-full h-full object-cover" />
                </div>

                <div>
                  <h3 className="text-xl font-semibold text-hc-700">{d.name}</h3>
                  <p className="text-sm text-hc-600">{d.role}</p>
                </div>
              </div>

              <p className="mt-4 text-hc-700">{d.bio}</p>

              <div className="mt-6 flex gap-3">
                <button
                  className="px-4 py-2 rounded-full text-sm font-medium btn-gloss inline-block"
                >
                  View Profile
                </button>
                <button
                  className="px-4 py-2 rounded-full text-sm font-medium border border-red-200 text-red-700 bg-white/80"
                >
                  Contact
                </button>
              </div>
            </motion.article>
          ))}
        </motion.div>

        {/* small credits / project footer */}
        <motion.footer
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.6 }}
          className="mt-12 text-center text-sm text-muted"
        >
          Built with React, Tailwind, Framer Motion and MongoDB — perfect for showcasing on LinkedIn.
        </motion.footer>
      </section>
    </main>
  );
}
