// src/pages/About.jsx
import React from "react";
import { motion } from "framer-motion";

const devs = [
  { name: "Jeffin Josva", role: "Lead Developer", img: "https://i.pravatar.cc/150?img=7" },
  { name: "Co-Developer", role: "Backend Engineer", img: "https://i.pravatar.cc/150?img=10" }
];

export default function About() {
  return (
    <main className="min-h-[calc(100vh-72px)] p-8 bg-gradient-to-br from-hc-50 to-white">
      <section className="text-center mb-10">
        <h1 className="text-4xl font-bold text-red-700 mb-3">About HealthChain</h1>
        <p className="max-w-2xl mx-auto text-muted">HealthChain is a demo platform to manage patient records, appointments and prescriptions with a modern UI.</p>
      </section>

      <section className="flex justify-center gap-8">
        {devs.map((d, i) => (
          <motion.div key={d.name} initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ delay: i * 0.12 }} className="bg-white p-6 rounded-xl shadow-xl w-72 text-center hover:scale-105">
            <img src={d.img} alt={d.name} className="w-24 h-24 rounded-full mx-auto mb-4 border-4 border-red-200" />
            <h3 className="text-xl font-bold text-red-700">{d.name}</h3>
            <p className="text-muted">{d.role}</p>
          </motion.div>
        ))}
      </section>
    </main>
  );
}
