import React from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import Navbar from "../components/Navbar";

export default function Welcome() {
  const navigate = useNavigate();

  return (
    <div>
      <Navbar />
      <main className="min-h-[calc(100vh-80px)] flex flex-col items-center justify-center px-6">
        <motion.div
          initial={{ scale: 0.98, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
          className="text-center"
        >
          <h1 className="text-5xl md:text-6xl font-extrabold text-red-700 mb-4">
            Welcome to HealthChain
          </h1>
          <p className="max-w-2xl mx-auto text-gray-700 mb-8">
            Securely manage health records, appointments, prescriptions and connect with your doctor.
          </p>

          <div className="flex gap-4 justify-center">
            <button
              onClick={() => navigate("/select-role")}
              className="btn-primary px-8 py-3 rounded-full font-semibold"
            >
              Get Started
            </button>

            <button
              onClick={() => navigate("/about")}
              className="px-6 py-3 rounded-full border border-red-600 text-red-700 bg-white/80"
            >
              Learn More
            </button>
          </div>
        </motion.div>
      </main>
    </div>
  );
}
