// frontend/src/pages/PatientDetails.jsx
import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import api from "../services/api";
import { format } from "date-fns";

/**
 * PatientDetails.jsx
 * - Location: frontend/src/pages/PatientDetails.jsx
 * - Requires: src/services/api.js (axios baseURL -> http://localhost:5000/api)
 * - Styling: Tailwind CSS
 *
 * UX:
 *  - three-column responsive layout
 *  - left: bio + quick actions
 *  - center: medical history list + prescriptions
 *  - right: sticky doctor cards (primary + assistant) with contact buttons
 */

const FALLBACK = {
  patient: {
    name: "Madhan Kumar",
    age: 29,
    sex: "Male",
    bloodGroup: "B+",
    allergies: ["Penicillin"],
    emergencyContact: { name: "S. Kumar", relation: "Father", phone: "+91-9999999999" },
    avatar: "https://i.pravatar.cc/150?img=12",
    medicines: ["Paracetamol 500mg", "Azithromycin 500mg"],
    currentStatus: { status: "Fever", notes: "High temperature, advised rest", since: new Date().toISOString() },
    assignedDoctor: { name: "Dr. S. Raj", specialty: "General Physician", phone: "+91-1111111111", avatar: "https://i.pravatar.cc/150?img=3" },
    assistantDoctor: { name: "Dr. Anita Sharma", specialty: "Internal Medicine", phone: "+91-2222222222", avatar: "https://i.pravatar.cc/150?img=5" },
    email: "madhan@example.com",
    phone: "+91-9876543210",
  },
  medicalHistory: [
    { _id: "h1", date: new Date().toISOString(), condition: "Seasonal flu", notes: "Rest & antivirals" },
    { _id: "h2", date: new Date("2023-02-20").toISOString(), condition: "Fractured wrist", notes: "Cast applied - healed" }
  ],
  prescriptions: [
    {
      _id: "p1",
      createdAt: new Date().toISOString(),
      meds: [
        { name: "Paracetamol", dosage: "500mg", frequency: "3x/day", duration: "5 days" },
        { name: "Azithromycin", dosage: "500mg", frequency: "Once daily", duration: "3 days" }
      ],
      notes: "Drink fluids, rest",
      pharmacyStatus: "sent",
      deliveryETA: new Date(Date.now() + 4 * 3600 * 1000).toISOString(),
      doctor: { name: "Dr. S. Raj", avatar: "https://i.pravatar.cc/150?img=3" }
    }
  ],
  appointments: []
};

function Pill({ text }) {
  return <span className="inline-block px-3 py-1 text-xs rounded-full bg-white/10 text-white/90">{text}</span>;
}

export default function PatientDetails() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    let mounted = true;
    async function fetchProfile() {
      setLoading(true);
      setError("");
      try {
        const res = await api.get("/patients/me");
        if (!mounted) return;
        setData(res.data);
      } catch (err) {
        console.error("PatientDetails fetch err:", err);
        setError("Failed to load patient data. Showing demo fallback.");
        setData(FALLBACK);
      } finally {
        if (mounted) setLoading(false);
      }
    }
    fetchProfile();
    return () => { mounted = false; };
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-red-700 via-red-600 to-red-800 text-white">
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ repeat: Infinity, duration: 1.4, ease: "linear" }}
          className="w-14 h-14 rounded-full border-4 border-white/25 flex items-center justify-center"
        >
          <div className="w-6 h-6 rounded-full bg-white/90" />
        </motion.div>
      </div>
    );
  }

  const { patient, medicalHistory = [], prescriptions = [] } = data || FALLBACK;

  const since = patient?.currentStatus?.since ? format(new Date(patient.currentStatus.since), "dd MMM yyyy") : "N/A";

  return (
    <div className="min-h-screen p-6 bg-gradient-to-br from-red-700 via-red-600 to-red-800 text-white">
      <div className="max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-12 gap-6">
        {/* LEFT: Bio & quick actions */}
        <motion.aside
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5 }}
          className="lg:col-span-3 bg-white/6 backdrop-blur p-6 rounded-2xl border border-white/10"
        >
          <div className="flex items-center gap-4">
            <img src={patient.avatar} alt={patient.name} className="w-20 h-20 rounded-full object-cover border-4 border-white/20 shadow" />
            <div>
              <h2 className="text-2xl font-bold">{patient.name}</h2>
              <p className="text-sm text-white/80">{patient.email}</p>
              <p className="text-sm text-white/80">{patient.phone}</p>
            </div>
          </div>

          <div className="mt-4">
            <div className="flex gap-2 items-center">
              <Pill text={`${patient.age ?? "-" } yrs`} />
              <Pill text={patient.sex || "-"} />
              <Pill text={`Blood: ${patient.bloodGroup || "-"}`} />
            </div>

            <div className="mt-4">
              <h3 className="text-sm text-white/90 font-semibold">Current Status</h3>
              <p className="mt-1 text-white/80">{patient.currentStatus?.status || "Healthy"}</p>
              <p className="text-xs text-white/70 mt-1">{patient.currentStatus?.notes || ""}</p>
              <p className="text-xs text-white/70 mt-1">Since: {since}</p>
            </div>

            <div className="mt-4">
              <h3 className="text-sm text-white/90 font-semibold">Allergies</h3>
              <p className="mt-1 text-white/80">{(patient.allergies && patient.allergies.join(", ")) || "None"}</p>
            </div>

            <div className="mt-4">
              <h3 className="text-sm text-white/90 font-semibold">Emergency Contact</h3>
              <p className="mt-1 text-white/80">{patient.emergencyContact?.name} ({patient.emergencyContact?.relation})</p>
              <p className="text-xs text-white/70">{patient.emergencyContact?.phone}</p>
            </div>

            <div className="mt-6 flex flex-col gap-3">
              <button className="py-2 px-3 rounded-lg bg-gradient-to-r from-red-500 to-red-700 shadow hover:scale-105 transition">Message Doctor</button>
              <button className="py-2 px-3 rounded-lg bg-white/10 hover:bg-white/20 transition">Request Appointment</button>
              <button className="py-2 px-3 rounded-lg bg-white/10 hover:bg-white/20 transition">Download Records (PDF)</button>
            </div>
          </div>
        </motion.aside>

        {/* CENTER: Medical history & prescriptions */}
        <main className="lg:col-span-6 space-y-6">
          <motion.section initial={{ opacity: 0, y: 10 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.4 }} className="bg-white/6 backdrop-blur p-6 rounded-2xl border border-white/10">
            <div className="flex items-center justify-between">
              <h3 className="text-xl font-bold">Medical Records</h3>
              <span className="text-sm text-white/70">{medicalHistory.length} records</span>
            </div>

            <div className="mt-4 divide-y divide-white/8">
              {medicalHistory.length === 0 ? (
                <p className="text-white/70 py-4">No history available.</p>
              ) : (
                medicalHistory.map((h, idx) => (
                  <motion.article key={h._id || idx} whileHover={{ scale: 1.01 }} className="py-4 flex justify-between items-start gap-4">
                    <div>
                      <div className="text-sm text-white/90 font-semibold">{h.condition}</div>
                      <div className="text-xs text-white/70 mt-1">{h.notes}</div>
                    </div>
                    <div className="text-xs text-white/70">{h.date ? format(new Date(h.date), "dd MMM yyyy") : ""}</div>
                  </motion.article>
                ))
              )}
            </div>
          </motion.section>

          <motion.section initial={{ opacity: 0, y: 12 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.5 }} className="bg-white/6 backdrop-blur p-6 rounded-2xl border border-white/10">
            <div className="flex items-center justify-between">
              <h3 className="text-xl font-bold">Latest Prescriptions</h3>
              <span className="text-sm text-white/70">{prescriptions.length} items</span>
            </div>

            {prescriptions.length === 0 ? (
              <p className="text-white/70 mt-4">No prescriptions yet.</p>
            ) : (
              <div className="mt-4 space-y-4">
                {prescriptions.map((pres) => (
                  <motion.div key={pres._id} whileHover={{ y: -4 }} className="p-4 rounded-lg bg-white/8 border border-white/6">
                    <div className="flex items-start gap-4">
                      <img src={pres.doctor?.avatar || "/images/placeholder-avatar.png"} alt={pres.doctor?.name} className="w-12 h-12 rounded-full object-cover border-2 border-white/10" />
                      <div className="flex-1">
                        <div className="flex items-center justify-between">
                          <div>
                            <div className="text-sm font-semibold">{pres.doctor?.name || "Unknown"}</div>
                            <div className="text-xs text-white/70">{pres.notes || ""}</div>
                          </div>
                          <div className="text-xs text-white/70">{pres.createdAt ? format(new Date(pres.createdAt), "dd MMM yyyy") : ""}</div>
                        </div>

                        <ul className="mt-3 ml-1 space-y-1 text-white/90">
                          {pres.meds?.map((m, i) => (
                            <li key={i} className="text-sm">• <span className="font-medium">{m.name}</span> — {m.dosage} · {m.frequency} · {m.duration}</li>
                          ))}
                        </ul>

                        <div className="mt-3 flex items-center gap-3">
                          <span className={`text-xs px-2 py-1 rounded-full ${pres.pharmacyStatus === "sent" ? "bg-yellow-500/20" : pres.pharmacyStatus === "delivered" ? "bg-green-500/20" : "bg-white/8"}`}>
                            {pres.pharmacyStatus || "pending"}
                          </span>
                          {pres.deliveryETA && <span className="text-xs text-white/70">ETA: {format(new Date(pres.deliveryETA), "HH:mm, dd MMM")}</span>}
                          <button className="ml-auto text-sm bg-red-500/80 px-3 py-1 rounded-lg hover:scale-105 transition">Request Refill</button>
                        </div>
                      </div>
                    </div>
                  </motion.div>
                ))}
              </div>
            )}
          </motion.section>
        </main>

        {/* RIGHT: Sticky Doctor cards */}
        <motion.aside initial={{ opacity: 0, x: 20 }} animate={{ opacity: 1, x: 0 }} transition={{ duration: 0.5 }} className="lg:col-span-3">
          <div className="sticky top-24 space-y-4">
            {/* Primary doctor */}
            <div className="bg-white/6 p-4 rounded-2xl border border-white/10">
              <div className="flex items-center gap-3">
                <img src={patient.assignedDoctor?.avatar || "/images/placeholder-avatar.png"} alt={patient.assignedDoctor?.name} className="w-16 h-16 rounded-full object-cover border-2 border-white/10" />
                <div>
                  <div className="text-lg font-semibold">{patient.assignedDoctor?.name || "Primary Doctor"}</div>
                  <div className="text-sm text-white/80">{patient.assignedDoctor?.specialty || ""}</div>
                </div>
              </div>
              <div className="mt-3 flex gap-2">
                <a href={`tel:${patient.assignedDoctor?.phone || "#"}`} className="flex-1 text-center py-2 rounded-lg bg-gradient-to-r from-red-500 to-red-700">Call</a>
                <button className="flex-1 py-2 rounded-lg bg-white/10">Message</button>
              </div>
            </div>

            {/* Assistant doctor */}
            <div className="bg-white/6 p-4 rounded-2xl border border-white/10">
              <div className="flex items-center gap-3">
                <img src={patient.assistantDoctor?.avatar || "/images/placeholder-avatar.png"} alt={patient.assistantDoctor?.name} className="w-16 h-16 rounded-full object-cover border-2 border-white/10" />
                <div>
                  <div className="text-lg font-semibold">{patient.assistantDoctor?.name || "Assistant Doctor"}</div>
                  <div className="text-sm text-white/80">{patient.assistantDoctor?.specialty || ""}</div>
                </div>
              </div>
              <div className="mt-3 flex gap-2">
                <a href={`tel:${patient.assistantDoctor?.phone || "#"}`} className="flex-1 text-center py-2 rounded-lg bg-gradient-to-r from-red-500 to-red-700">Call</a>
                <button className="flex-1 py-2 rounded-lg bg-white/10">Message</button>
              </div>
            </div>

            {/* Small stats */}
            <div className="bg-white/6 p-4 rounded-2xl border border-white/10 text-center">
              <div className="text-sm text-white/80">Platform Stats</div>
              <div className="mt-2 text-2xl font-bold">1,000+</div>
              <div className="text-xs text-white/70">Doctors • 70,000+ Patients</div>
            </div>
          </div>
        </motion.aside>
      </div>

      {error && (
        <div className="max-w-7xl mx-auto mt-6 text-yellow-200">
          {error}
        </div>
      )}
    </div>
  );
}
