import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { samplePatient } from "../utils/patientData";
import Navbar from "../components/Navbar";
import { motion } from "framer-motion";

export default function SignIn() {
  const navigate = useNavigate();
  const [role, setRole] = useState(null);
  const [form, setForm] = useState({ username: "", password: "" });
  const [error, setError] = useState("");

  useEffect(() => {
    const r = localStorage.getItem("hc_selectedRole");
    if (!r) {
      navigate("/select-role", { replace: true });
      return;
    }
    setRole(r);
  }, [navigate]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    // For now: validate with samplePatient only if role === 'patient'
    if (role !== "patient") {
      alert("Only Patient flow is enabled in this demo.");
      return;
    }

    if (form.username === samplePatient.username && form.password === samplePatient.password) {
      // store logged in
      localStorage.setItem("hc_isLoggedIn", "true");
      localStorage.setItem("hc_userId", samplePatient._id);
      navigate(`/dashboard`);
    } else {
      setError("Invalid username or password. (Demo credentials: madhan / madhan123)");
    }
  };

  if (!role) return null;

  return (
    <div>
      <Navbar />
      <section className="min-h-[calc(100vh-80px)] flex items-center justify-center px-4 bg-gradient-to-br from-hc-600 via-hc-500 to-hc-700 text-white">
        <motion.div initial={{ y: -10, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ duration: 0.5 }} className="bg-white p-8 rounded-2xl shadow-2xl text-red-700 w-full max-w-md">
          <h2 className="text-2xl font-bold mb-2 text-center">Sign in as <span className="uppercase">{role}</span></h2>
          <p className="text-sm text-muted text-center mb-6">For demo: username <strong>madhan</strong> and password <strong>madhan123</strong></p>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-gray-700 text-sm mb-1">Username</label>
              <input name="username" value={form.username} onChange={handleChange} className="w-full px-4 py-2 border rounded-lg" />
            </div>

            <div>
              <label className="block text-gray-700 text-sm mb-1">Password</label>
              <input name="password" type="password" value={form.password} onChange={handleChange} className="w-full px-4 py-2 border rounded-lg" />
            </div>

            {error && <div className="text-red-600 text-sm">{error}</div>}

            <button type="submit" className="w-full btn-primary rounded-full py-2 text-white font-semibold">Sign In</button>
          </form>
        </motion.div>
      </section>
    </div>
  );
}
